package Interview;

public class AlphabetCheck {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char ch = '$';
		
		if((ch>='a' && ch <='z') || (ch>='A' && ch <='Z')) 
			
			System.out.println(ch+" is alphabet");
		
		else
			System.out.println(ch+" is not alphabet");

	}

}
