package Interview;

public class MultiplicationTable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// 2x1=2
		// 2x2=4
		
		
		int num = 10;
		int mul = 0;
		
		for(int i=1; i<=10; i++) {
			
			mul = num * i;
			System.out.println(num+"x" +i+"="+mul);
			
		}
		
		

	}

}
